INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '15', '1', '788271000', '0', '0', 'dendy_new_reality_15', 'http://vk.com/video_ext.php?oid=30190780&id=165131908&hash=ec995f486be8970e&hd=1', '', '', 'Передача Денди - новая реальность', '', 'Dendy - Новая Реальность #15', '<p>В этом выпуске:</p><ul class=\"file-info\"><li><span>Dendy:</span> Power Blade, The Universe Soldiers, Jumping Kid</li><li><span>SNES:</span> World Heroes, Tiny Toon Adventures - Buster Busts Loose!</li><li><span>Награждения</span></li><li><span>Лотерея! </span>Картридж <span lang=\"en-US\">Sky Destroyer </span><span lang=\"ru-RU\">для Денди выиграл Мальцев В.В, г. Тучково Московской области.</span><span lang=\"ru-RU\"></span> </li><li><span>Sega:</span> Urban Strike. Картридж и коробка Urban Strike фирменные, оригинальной формы и с мануалом!</li><li><span>Люди:</span> Олег Шевченко из Воронежа, выиграл в фанклабе журнала Sega CD-X + 3 диска. Для этого описал 3 игры для Денди и 2 для Сеги. Саша Ходячий выиграл Hyper Shot и 3 карика<span lang=\"en-US\">-</span><span lang=\"ru-RU\">стрелялки</span> (Денди).</li><li>Письма: от Курбатова Глеба - попросил сравнить <span lang=\"en-US\">SNES </span>и Сегу. Сосунов Алексей из г. Электросталь. Спросил про добротность Кен-Бой и Битман.</li></ul>', '', '', '', '', '', 'Dendy, Sorec Video', 'http://tv-games.ru/wiki/Show/Dendy-NewReality.html', '', 'images/video/dnr/15.jpg', '', '', '', '849', '0', '0', '0', '0', 'yes', 'all', '0', '0', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '16', '1', '788875800', '0', '0', 'dendy_new_reality_16', 'http://vk.com/video_ext.php?oid=30190780&id=165131926&hash=a8d9a5aede6268e2&hd=1', '', '', 'Передача Денди - новая реальность', '', 'Dendy - Новая Реальность #16', '<p>В этом выпуске:</p><ul class=\"file-info\"><li><span>Dendy:</span> Batman Returns, Adventure Island 2</li><li><span>Лотерея!</span><span lang=\"ru-RU\"> Ахачев А.А. Московская область, г. Наро-Фоминск выиграл карик </span>Road Fighter (NES). </li><li><span>Кто? Где? Куда?:</span> The Magical Quest Starring Mickey Mouse (SNES), Tiny Toon Adventures - Buster Busts Loose! (SNES)</li><li><span>SNES:</span> Sparkster, Super Castlevania IV, Super Mario Kart</li><li><span>Люди:</span> Владимир Сазонович Кирюшов (артист Московского центра Эксцентрики, придумал песенку)<br />- Что Такой Денди, что такое Денди, что так любит детвора?<br />- Что Такой Денди, что такое Денди - это электронная игра.<br />- В доме однажды увидит с утра чудо такое ребёнок.<br />- Будто из Африки в дом пришагал маленький слонёнок.<br />- Что Такой Денди, что такое Денди, что так любит детвора?<br />- Что Такой Денди, что такое Денди - это электронная игра.<br />Так же Денис Панов.</li><li>Супонев сказал, что это 17-й выпуск (31.12.1994)!!! Обещал, что к НГ ожидается наплыв картриджей SNES. Так же обещал, что к НГ все, кто в базе данных бесплатно получат по почте буклеты о Super NES.</li></ul>', '', '', '', '', '', 'Dendy, Sorec Video', 'http://tv-games.ru/wiki/Show/Dendy-NewReality.html', '', 'images/video/dnr/16.jpg', '', '', '', '1038', '0', '0', '1', '5', 'yes', 'all', '0', '0', '');
