INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '26', '1', '794923800', '0', '0', 'dendy_new_reality_26', 'http://vk.com/video_ext.php?oid=30190780&id=165132173&hash=92a0ef286b762f73&hd=1', '', '', 'Передача Денди - новая реальность', '', 'Dendy - Новая Реальность #26', '<p>В этом выпуске:</p><ul class=\"file-info\"><li><span>Dendy:</span> Teenage Mutant Ninja Turtles, Tecmo World Cup Soccer, TV Game 5-in-1: The Goonies</li><li><span>Sega:</span> \"Gun Fighter\" (Lethal Enforces), Chester Cheetah 2 - Wild Wild Quest</li><li><span>SNES:</span> Metal Marines, Bubsy in Claws Encounters of the Furred Kind, Super Mario World</li><li>Прорекламирован Super Police Fighter от фирмы Megatron (розовенький и голубенький пистолеты для Сеги)</li><li>Предложено нарисовать гепарда Честера, т.к. о самой игре мало чего удалось рассказать.</li></ul>', '', '', '', '', '', 'Dendy, Sorec Video', 'http://tv-games.ru/wiki/Show/Dendy-NewReality.html', '', 'images/video/dnr/26.jpg', '', '', '', '610', '0', '0', '1', '1', 'yes', 'all', '0', '4', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '27', '1', '795528600', '0', '0', 'dendy_new_reality_27', 'http://vk.com/video_ext.php?oid=30190780&id=165132175&hash=92867c81f900bc57&hd=1', '', '', 'Передача Денди - новая реальность', '', 'Dendy - Новая Реальность #27', '<p>В этом выпуске:</p><ul class=\"file-info\"><li><span>Dendy:</span> Chessmaster, Sky Destroyer</li><li><span>Поход в магазин \"Dendy\" на Ленинском проспекте 99</span></li><li><span>Sega:</span> Aero the Acro-Bat</li><li><span>SNES:</span> Rise of the Robots, Eek the Cat (тёмная графика), Blackhawk</li><li>Буклеты на русском: Парк Юрского Периода и Mortal Kombat (Смертельная Схватка)</li><li>Дети по-прежнему мечтают о Мегадрайве 2, хотя и признают Супер Нинтендо крутой, но увы, недосягаемой (по цене). Donkey Kong назван самой продаваемой игрой. Поставщиком игр был назван Стиплер (со слов продавца).</li></ul>', '', '', '', '', '', 'Dendy, Sorec Video', 'http://tv-games.ru/wiki/Show/Dendy-NewReality.html', '', 'images/video/dnr/27.jpg', '', '', '', '638', '0', '0', '0', '0', 'yes', 'all', '0', '1', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '28', '1', '796133400', '0', '0', 'dendy_new_reality_28', 'http://vk.com/video_ext.php?oid=30190780&id=165132177&hash=24917b0f5f02c702&hd=1', '', '', 'Передача Денди - новая реальность', '', 'Dendy - Новая Реальность #28', '<p>По-настоящему революционный, \"Геймбоевский\" выпуск.</p><ul class=\"file-info\"><li><span>Dendy:</span> Predator, Chip & Dale 2 - оба видео описания игр из  14-го выпуска!!!</li><li><span>Sega:</span> Indiana Jones and the Last Crusade, Super Shinobi II, The</li><li><span>Поход в магазин \"Dendy\" на проспекте Вернадского 109.</span> Ужас. Во многих интервью молодёжь и взрослые жуют жвачку! Мальчик Алёша за 538500 рублей купил себе Сегу. </li><li><span>SNES:</span> Super Battle Tank 2, Yoshi\'s Safari, Brutal - Paws of Fury</li><li><span>Game Boy:</span> Официальный рассказ про приставку. Один из детей обозвал GB тэтрисом :)</li><li><span>Цитата:</span> Водка-плохо, Супер Нинтендо-хорошо!</li><li>Было объявлено о весенней распродаже Денди: Dendy Junior 99000 рублей, Mega Drive II 530000 руб. Алёша переплатил 8500 рублей...</li><li>Показан интересный ролик о том, как отличить товары Денди от подделок. Ещё два ролика про Геймбой, который \"всегда с тобой\".</li><li>Был показан и беспроводной Super Nintendoscope на примере игры Yoshi\'s Safari.</li></ul>', '', '', '', '', '', 'Dendy, Sorec Video', 'http://tv-games.ru/wiki/Show/Dendy-NewReality.html', '', 'images/video/dnr/28.jpg', '', '', '', '692', '0', '0', '0', '0', 'yes', 'all', '0', '0', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '29', '1', '796738200', '0', '0', 'dendy_new_reality_29', 'http://vk.com/video_ext.php?oid=30190780&id=165132178&hash=e3b7df65a6db8a23&hd=1', '', '', 'Передача Денди - новая реальность', '', 'Dendy - Новая Реальность #29', '<p>В этом выпуске:</p><ul class=\"file-info\"><li><span>Dendy:</span> Adventures in the Magic Kingdom (прохождение ужас), Super Mario Bros. 3, \"Mickey Mouse 4\" (Mickey\'s Safari in Letterland)</li><li><span>Sega:</span> Cosmic Spacehead</li><li><span>SNES:</span> Choplifter III, Mortal Kombat II, Yogi Bear</li><li><span>Game Boy:</span> Очередная реклама. Об F1 Race было устно рассказано и показан адаптер, позволяющий играть вчетвером</li><li><span>Цитата:</span> ...картридж производства фирмы Genesis - известная, снискавшая невероятную славу фирма... (о карике Cosmic Spacehead). Правда, уже после показа обзора игры ведущий уточнил, что Genesis на самом деле американская Сега.</li><li><span>Журналы:</span> Видео-Асс Dendy Новая Реальность №17</li><li><span>Цитата:</span> На обложке нарисован страшный Робокоп (речь о Дарте Вейдере)</li></ul>', '', '', '', '', '', 'Dendy, Sorec Video', 'http://tv-games.ru/wiki/Show/Dendy-NewReality.html', '', 'images/video/dnr/29.jpg', '', '', '', '621', '0', '0', '0', '0', 'yes', 'all', '0', '0', '');
