INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '97', '8', '1320717600', '0', '0', 'ot_vinta_7', 'http://vk.com/video_ext.php?oid=-295321&id=145787739&hash=01fe0b8c3deae670&hd=2', '', '', 'От Винта с Бонусом и Гамовером', '', 'От винта! - выпуск 7', '<p>Вторник, и мы снова с вами. Этот выпуск вас точно не разочарует как подборкой игр, так и нестандартными описаниями. Прикольно всё-таки играть в MK под \"танец с саблями\" Хачатуряна :) Мы познакомимся с тремя победителями MK - они восприняли игру слишком буквально :) А ещё мы прочтём письмо одного космонавта</p><p>В номере: <strong>Mortal Kombat, Bioforge </p><p>Цитаты: \"...Горо: имеет несколько рук как Будда и причёску конский хвост как Стивен Сигал\"... ...\"Одна пустота да звёзды. Вот бы их все на коньяк...\"</p>', '', '', '', '', '', 'Игромания', 'http://igromania.ru', '', 'images/video/ot_vinta.png', '', '', '', '1223', '0', '0', '2', '6', 'yes', 'all', '0', '0', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '83', '4', '1319914080', '0', '0', 'tetris_from_russia_with_love', 'http://vk.com/video_ext.php?oid=30190780&id=165153571&hash=27de3319453da5d5&hd=1', '', '', 'Tetris: From Russia with Love', '', 'TETЯIS: Из России с любовью', '<p>Tetris: From Russia with Love от Ricochet TV - потрясающее часовое видео, рассказывающее об одной из самых популярных игр всех времён - Тетрисе. Это история четырёх людей, двух систем и одной игры...</p>', '<p>Я ни в коем случае не претендую на авторство передачи, выкладываю в ознакомительных целях.<br />http://www.ricochet.co.uk/program/Tetris-From-Russia-with-Love_295.aspx</p>', '', '', '', '', '', '', '', 'images/video/edu/tetris.png', '', '', '', '766', '0', '0', '11', '51', 'yes', 'all', '0', '1', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '84', '4', '1319914080', '0', '0', 'story_of_video_games', 'http://vk.com/video_ext.php?oid=30190780&id=165155598&hash=20abc5a0ac54f90a&hd=1', '', '', 'The Story of Computer Games', '', 'История компьютерных игр', '<p>The Story of Computer Games от режиссёра Hugh Whitworth это история о необычных людях: мечтателях, бунтарях и вундеркиндах. Два десятилетия они вели ожесточённую конкурентную борьбу, судебные споры и плели международные интриги. И всё это чтобы стать путеводными звёздами в новой области мировой промышленности. Сегодня во всём мире эта многомиллиардная отрасль промышленности приносит больше прибыли чем Голливуд и скоро отнимет у музыкальной индустрии звание самого доходного развлекательного бизнеса на планете. Этот бизнес - компьютерные игры.<br /><br />В фильме рассказана история компании Atari и игры Pong, компании Code Masters и близнецов Оливер, Шигеру Миямото и его Марио и Зельды, Алексея Пажитнова и его игры Tetris...</p>', '<p>Я ни в коем случае не претендую на авторство передачи, шла она в своё  время по телеку, откуда и была записана кем-то. Выкладываю в  ознакомительных целях.</p>', '', '', '', '', '', '', '', 'images/video/edu/vg_story.png', '', '', '', '852', '0', '0', '7', '31', 'yes', 'all', '0', '0', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '95', '8', '1320458400', '0', '0', 'ot_vinta_5', 'http://vk.com/video_ext.php?oid=-295321&id=145780332&hash=51e9c158d28e382e&hd=2', '', '', 'От Винта с Бонусом и Гамовером', '', 'От винта! - выпуск 5', '<p>В качестве исключения, сегодня поделюсь 5-м выпуском передачи. Синхронизируемся, так сказать. Сотни MOD\'ов Wolfenstein 3D и Doom сейчас уже не вызывает должной реакции, а вот в 1995 году DOOMмания ещё набирала обороты. Заценим и мы Wolfenstein 3D с доберманами синего цвета, DooM Alien и DOOM&WOLF. Мало? Тогда познакомимся ещё с прототипом системы Motion Capture;</p><p>В номере: <strong>На закуску получаем Corridor 7 и гоночки -  Nascar RACE.</p>', '', '', '', '', '', 'Игромания', 'http://igromania.ru', '', 'images/video/ot_vinta.png', '', '', '', '1362', '0', '0', '3', '11', 'yes', 'all', '0', '0', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '85', '4', '1320068160', '0', '0', 'i_videogame_1', 'http://vk.com/video_ext.php?oid=30190780&id=165153778&hash=94d407aa2087e1d6&hd=1', '', '', 'I, Videogame - 1 часть', '', 'Я, Видеоигра - 1 выпуск - Большой палец', '<p>История революции видеоигр насчитывает десятилетия, начиная от простых забав 50-х годов до всеохватывающей мощной волны в современной массовой культуре, которая соперничает - и возможно скоро превзойдет - кино и телевидение. То, что в прошлом зарождалось как субкультура, разрослось и вышло за рамки жанров, став уникальной формой самовыражения, влияющей абсолютно на все - от современных военных столкновений до межличностных отношений.<br /><br />\"I, Videogame\" - это всестороннее последовательное исследование прошлого, настоящего и будущего видеоигр и геймеров. Начиная со времен одной из первых игр \"Pong\" и до наших дней, когда популярна \"Halo 2\", от Atari 2600 до Nintendo и PlayStation, фильм \"Я, Видеоигра\" рассказывает о людях, идеях и технологиях видеоигр и их влиянии на нашу жизнь. Эволюция игр напоминает эффект маятника - раньше игры имитировали общество, а теперь общество воспроизводит игровые сюжеты. Используя интервью с титанами игровой индустрии прошлого и настоящего, этот пятисерийный фильм оценивает эволюцию видеоигр и их культурное влияние на современный мир развлечений.<br /><br />\"Я, Видеоигра - это провокационный \"рокументальный\" (от слова \"рок\") фильм о зарождении нового вида развлечений и влиянии на него социальной, политической и культурной жизни\", - сказала Ребекка Баттис, исполнительный вице-президент и начальник департамента креативного развития и бенд-менеджмента компании Discovery Networks International. \"Предлагая новую ретроспективу истории и технологии игр, эти серии демонстрируют приверженность канала Discovery к исследованию событий, тенденций и процессов, формирующих современный мир\".</p>', '<p>Исполнительными продюссерами фильма \"Я, Видеоигра\" были Роберт Карран из  компании \"Discovery Networks International\" и Фентон Бэйли и Рэнди  Барбато из \"World of Wonder\".<br /> <br /> В ролях:<br /> Исполнители:<br /> Джеймс Вагнер Ву (Журналист, пишущий о видеоиграх)<br /> Ральф Байер (Автор, Magnavox Odyssey)<br /> Ричард Бартл (Соавтор жанра MUD)<br /> Нолан Бушнел (Учредитель, Atari)<br /> Эд Халтер (Автор книги \"From Sun Tzu to Xbox\")<br /> Тору Иватани (Автор, Pac-Man)<br /> Генри Дженкинс (Директор программы \"Сравнительное изучение средств массовой информации\")<br /> Томохиро Нисикадо (Автор, Space Invaders)<br /> Алексей Пажитнов (Автор, Тетрис)<br /> Уилл Райт (Автор, Sim City и The Sims)</p>', '', '', '', '', '', '', '', 'images/video/edu/I_Videogame_1.jpg', '', '', '', '1151', '0', '0', '6', '26', 'yes', 'all', '0', '0', '');
