INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '91', '8', '1320068160', '0', '0', 'ot_vinta_1', 'http://vk.com/video_ext.php?oid=-295321&id=145752615&hash=c13fd0f36fdac305&hd=2', '', '', 'От Винта с Бонусом и Гамовером', '', 'От винта! - выпуск 1', '<p>Привет друзья! Сегодня мы начинаем показ целого цикла передач прошлого. От винта - телевизионная передача, посвящённая компьютерным играм, выходившая на  российском TV с 20 мая 1995 по 25 октября 1998 года. Авторы и ведущие: Борис Репетур aka Бонус, Антон Зайцев aka Гамовер и Сергей Чихачев, который периодически появлялся в различных образах, а также комментировал игровой процесс.</p><p>В этом выпуске будет рассказано о лучших играх прошлых лет: <strong>Wolfenstein 3D, Comanche, Master of Orion</strong>.</p>', '', '', '', '', '', 'Игромания', 'http://igromania.ru', '', 'images/video/ot_vinta.png', '', '', '', '8486', '0', '0', '17', '58', 'yes', 'all', '0', '6', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '92', '8', '1320217320', '0', '0', 'ot_vinta_2', 'http://vk.com/video_ext.php?oid=-295321&id=145770057&hash=7239698e3f6a70fd&hd=2', '', '', 'От Винта с Бонусом и Гамовером', '', 'От винта! - выпуск 2', '<p>А вот и вторая передача с Бонусом и Гамовером. Встречаем. Сегодня мы узнаем что такое \"плоскосмотр\", \"игровид\", \"внутрисуй\" и \"тулка (АК47)\".А заодно нам расскажут об опасном вирусе-мутанте, но Дмитрий Лозинский, известный своим гениальный антивирусом, нас успокоит..</p><p>Игры: <strong>Betrayal at Krondor, Doom и Lost in Time</strong>.</p>', '', '', '', '', '', 'Игромания', 'http://igromania.ru', '', 'images/video/ot_vinta.png', '', '', '', '4676', '0', '0', '11', '26', 'yes', 'all', '0', '2', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '93', '8', '1320307200', '0', '0', 'ot_vinta_3', 'http://vk.com/video_ext.php?oid=-295321&id=145774030&hash=2ab745eb82fa2ef4&hd=2', '', '', 'От Винта с Бонусом и Гамовером', '', 'От винта! - выпуск 3', '<p>Третий, достаточно мощный выпуск \"От винта\" не заставил себя ждать. Двое ведущих в течении примерно 12 минут развлекают нас интеллектуальными шутками. Дмитрий Николаевич Лозинский (Aidstest) тоже кое-чем поделится.</p><p>Смотрим про <strong>Doom II и Alone in the Dark</strong>.</p>', '', '', '', '', '', 'Игромания', 'http://igromania.ru', '', 'images/video/ot_vinta.png', '', '', '', '1522', '0', '0', '4', '15', 'yes', 'all', '0', '0', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '94', '8', '1320372000', '0', '0', 'ot_vinta_4', 'http://vk.com/video_ext.php?oid=-295321&id=145777146&hash=ed73c682a6ba7301&hd=2', '', '', 'От Винта с Бонусом и Гамовером', '', 'От винта! - выпуск 4', '<p>Последний на этой неделе выпуск передачи \"От винта\" не самый лучший, и дело тут не в самой передаче. Рассмотрены не жаркие аркады, а электронный тир, военная стратегия и симулятор полёта. Немного позабавят результаты работы программы \"Elastic Reality\".</p><p>В номере: <strong>Mad Dog McCree, Iron Cross, Dogfight 80 Years of Aerial Warfare</strong>.</p>', '', '', '', '', '', 'Игромания', 'http://igromania.ru', '', 'images/video/ot_vinta.png', '', '', '', '1371', '0', '0', '2', '6', 'yes', 'all', '0', '0', '');
