INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '112', '13', '1321671600', '0', '0', 'gray_elefant', 'rJQr3tnjHlg', '', '18', 'Проклятие серого слонёнка', '', 'Проклятие серого слонёнка', '<p>Фильм рассказывает о появлении и распространении в России в 90-ых годах 8-битных приставок и всего, что с ними связано. Денди, Стиплер, приставки, клоны, картриджи, игры, аксессуары - всё это вы увидите здесь. Вспомним не только о том, во что и как мы играли, но и о том, через что нам пришлось пройти, чтобы поиграть! Что пришлось пережить рядовому геймеру, какие подлянки встречались ему на пути к 8-битным играм и чем его изо дня в день пичкал Китай-Бабай! Почему вместо Фамикомов и N.E.S\'ов мы играли в Лифу и Денди, а ГеймБой нам заменяли Тетрисы Brick Game? Почему вместо оригинальных игр мы играли в кучу перехаченного хлама? Ответы на эти и на многие другие вопросы - смотрите в фильме \"Проклятие серого слонёнка\"! В фильме присутствует нецензурная лексика, поэтому вам надо быть зарегистрированным пользователем, чтобы посмотреть его. Или отправляйтесь на Ютубовский канал автора Kinaman88, мы транслируем передачу с его разрешения.</p>', '', '', '', '', '', '', '', '', 'images/video/kinamania/prokl.png', '', '', '', '3741', '0', '0', '21', '85', 'yes', 'all', '0', '8', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '113', '9', '1321754400', '0', '0', 'mk2011_01', 'http://vk.com/video_ext.php?oid=30190780&id=165154427&hash=644cabc7a091560c&hd=1', '', '18', 'Mortal Kombat 2011 Walkthrough HD Смертельная Битва Видеопрохождение', '', 'Mortal Kombat 2011 #1', '<p>Полное видеопрохождение гениальной игры со всеми роликами и боями, на русском. Эпизод первый - <strong>Johnny Cage</strong>.<br /><br />Спустя годы легендарная серия Mortal Kombat возвращается такой, какой ее помнят и ждут тысячи игроков. Это жестокая, кровавая и беспощадная битва не на жизнь, а на смерть, битва, в которой может победить только сильнейший. Столетия понадобились Шао Кану, чтобы одержать верх над Рейденом и его союзниками. Однако перед лицом смерти Рейден все же смог использовать свой последний шанс спасти мир. Он отправил ментальное сообщение самому себе в прошлое, во времена первого турнира, когда силы добра еще были способны уничтожить Шао Кана... Игрокам предстоит принять участие в турнире \"Смертельной битвы\", узнать истинную историю мира Mortal Kombat и изменить прошлое, чтобы спасти будущее.</p>', '<ul><li>Разработчик игры: NetherRealm Studios</li><li>Перевод на русский: Alex Derek (ВОРОН)</li><li>Озвучка: BOPOH & Tauriel (2х голосая, закадровая)</li><li>Запись прохождения: ScorpionFantom</li><li>Раздача взята с rutracker.org</li></ul>', '', '', '', '', '', '', '', 'images/video/mk2011.png', '', '', '', '2791', '0', '0', '21', '75', 'yes', 'all', '0', '0', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '136', '13', '1322881200', '0', '0', 'dendy_chronicles_2', 'Unhavc-_WBE', '', '18', 'Dendy Chronicles #2', '', 'Dendy Chronicles #2', '<p>Вторая часть пилотного эпизода нового цикла обзоров \"Dendy Chronicles\" (\"Хроники Денди\"), являющегося продолжением фильма \"Проклятие серого слонёнка\". Первый эпизод посвящён вселенной Супер Марио, а конкретнее - всем играм о нём, когда-либо выходившим на Денди. В фильме присутствует нецензурная лексика, поэтому вам надо быть зарегистрированным пользователем, чтобы посмотреть его. Или отправляйтесь на Ютубовский канал автора Kinaman88, мы транслируем передачу с его разрешения.</p>', '', '', '', '', '', '', '', '', 'images/video/kinamania/dendy_cr.png', '', '', '', '725', '0', '0', '5', '21', 'yes', 'all', '0', '0', '');
