INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '139', '9', '1322100000', '0', '0', 'medal_wolfenstein', 'http://vk.com/video_ext.php?oid=30190780&id=165155569&hash=abf2b608afac3212&hd=1', '', '', 'Медаль за возвращение в замок Вольфенштейн', '', 'Медаль за возвращение в замок Вольфенштейн', '<p>Сегодня четверг, а значит должен быть следующий выпуск \"От Винта\". Но я прерываю показ цикла передач на один день, так как просто не могу не поделиться следующим видео материалом. Тем более, что 19-го выпуска передачи пока не нашли.</p><p>Сюжет: некий геймер попадает в виртуальность, встречает там Лару Крофт и вместе они крошат и шинкуют Вольфенштейнских немцев, которые опять замыслили жуткую гадость...<br /><br />Самое примечательное то, что фильм на протяжении 6 лет в одиночку создавал житель Владивостока - Александр Тимченко. На протяжении сюжета мы вспомним такие замечательные игры как \"Tomb Raider\", \"Return to the Castle of Wolfenstein\", \"Quake III\", \"Half Life\", \"Doom III\" и много других. В результате получаем захватывающее приключения со многими моментами, вызывающими неслабую ностальгию у геймеров. О тонком юморе автора и том, как это реализовано в 3D вообще ни слова, вы это должны увидеть сами. Смотреть всем.</p>', '<p>Выкладываю ссылку на \"Медаль за возвращение в замок Вольфенштейн\" только потому, что в восторге от увиденного. Я ни коем  образом не претендую на авторство и выкладываю для того, чтобы об этом  творении узнало как можно больше людей.</p><p>Режиссёр: Александр Тимченко<br />В ролях: Озвучивали: Александр  Тимченко, Елена Тимченко, Максим Мамонов, Юрий Ковалевич, Валерий  Максаков, Дина Оленевич, Алексей Шевченко, Юрий Мосинцев, Владимир  Шестопалов.</p>', '', '', '', '', 'Александр Тимченко', '', '', 'images/video/medal.png', '', '', '', '7658', '0', '0', '30', '130', 'yes', 'all', '0', '12', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '129', '9', '1322359200', '0', '0', 'mk2011_02', 'http://vk.com/video_ext.php?oid=30190780&id=165154548&hash=f5fe676a78aa0142&hd=1', '', '18', 'Mortal Kombat 2011 Walkthrough HD Смертельная Битва Видеопрохождение', '', 'Mortal Kombat 2011 #2', '<p>Полное видеопрохождение гениальной игры со всеми роликами и боями, на русском. Эпизод второй - <strong>Sonya Blade</strong>.<br /><br />Спустя годы легендарная серия Mortal Kombat возвращается такой, какой ее помнят и ждут тысячи игроков. Это жестокая, кровавая и беспощадная битва не на жизнь, а на смерть, битва, в которой может победить только сильнейший. Столетия понадобились Шао Кану, чтобы одержать верх над Рейденом и его союзниками. Однако перед лицом смерти Рейден все же смог использовать свой последний шанс спасти мир. Он отправил ментальное сообщение самому себе в прошлое, во времена первого турнира, когда силы добра еще были способны уничтожить Шао Кана... Игрокам предстоит принять участие в турнире \"Смертельной битвы\", узнать истинную историю мира Mortal Kombat и изменить прошлое, чтобы спасти будущее.</p>', '<ul><li>Разработчик игры: NetherRealm Studios</li><li>Перевод на русский: Alex Derek (ВОРОН)</li><li>Озвучка: BOPOH & Tauriel (2х голосая, закадровая)</li><li>Запись прохождения: ScorpionFantom</li><li>Раздача взята с rutracker.org</li></ul>', '', '', '', '', '', '', '', 'images/video/mk2011.png', '', '', '', '652', '0', '0', '6', '26', 'yes', 'all', '0', '0', '');
