INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '271', '17', '1352549400', '0', '0', 'emulators_001', 'U4df0JiSfBE', '', '', 'Передача Эмуляторы про старые игрушки', '', 'Эмуляторы 001 - Пилот', '<p>\"Эмуляторы\" - не простая передача об играх. Это история с хитро  замаскированным сюжетом, которая началась с намерения двух друзей  выпустить развлекательную программу о старых добрых приставочных играх.  Несмотря на некоторые разногласия, они медленно, но верно начинают  завоёвывать аудиторию.</p><p>Но когда берёшь на себя смелость вторгаться в чужой мир нужно быть  готовым, что кто-то может последовать твоему примеру. Постепенно герои  понимают, что некогда безобидная передача может привести к серьёзным и  неожиданным последствиям...</p><p>С е р и я № 1:<br />Два парня - Континуум и Селект открывают новую  программу об игровых приставках. В качестве приветствия они  демонстрируют обзоры на популярные игры прошлого столетия - \"Танчики\"  (Battle City), \"Зеро Толеранс\" (Zero Tolerance) и \"Король Лев\" (The Lion  King).</p>', '', '', '', '', '', 'NILinYT', 'http://www.youtube.com/user/NILinYT', '', 'images/video/emulators.png', '', '', '', '810', '0', '0', '11', '51', 'yes', 'all', '0', '5', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '272', '17', '1353154200', '0', '0', 'emulators_002', 's5VFvIm37ks', '', '', 'Передача Эмуляторы про старые игрушки', '', 'Эмуляторы 002 - Кто Первый?', '<p>С е р и я № 2:<br />Проект только стартовал, а у наших героев уже  назревает конфликт. Каждый ведущий пытается по-своему завоевать внимание  зрителя, постоянно перехватывая инициативу друг у друга. Кто же  всё-таки возьмёт верх - весельчак Геймер или обаятельный Зазнайка? На  этот раз, они поведуют о таких играх как Монстр в моём кармане, Дюна -  Битва за Арракис и Ф-Зеро.</p>', '', '', '', '', '', 'NILinYT', 'http://www.youtube.com/user/NILinYT', '', 'images/video/emulators.png', '', '', '', '744', '0', '0', '5', '21', 'yes', 'all', '0', '3', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '273', '17', '1353759000', '0', '0', 'emulators_003', '-dMJ3hiwc-0', '', '', 'Передача Эмуляторы про старые игрушки', '', 'Эмуляторы 003 - Черепашья радость ', '<p>С е р и я № 3:<br />После затянутой паузы и долгих споров Селект и  Континуум решаются осветить один из самых знаменитых квартетов 90-х  годов. Зелёные защитники в панцирях становятся гвоздём выпуска. Рядом с  ними за мир во всём мире борются отважные бойцы спецподразделения  &laquo;Контра&raquo; и космические наёмники Стар Фокс.</p>', '', '', '', '', '', 'NILinYT', 'http://www.youtube.com/user/NILinYT', '', 'images/video/emulators.png', '', '', '', '787', '0', '0', '4', '12', 'yes', 'all', '0', '1', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '274', '17', '1354363800', '0', '0', 'emulators_004', '4ehICQd4eC8', '', '', 'Передача Эмуляторы про старые игрушки', '', 'Эмуляторы 004 - Супергеройская история ', '<p>С е р и я № 4:<br />У Селекта радостное событие - вышел долгожданный фильм  \"Мстители\" по комиксам Марвел. Чем не повод отметить этот радостный  момент тематическим выпуском о любимых героях в компании с \"Капитаном  Америка и Мстителями\" (Captan America and the Avengers), \"Невероятным  Халком\" (The Incredible Hulk) и \"Супер героями Марвел\" (Marvel Super  Heroes: War of the Gems). Только вот Континуум сегодня, кажется, не в  духе...</p>', '', '', '', '', '', 'NILinYT', 'http://www.youtube.com/user/NILinYT', '', 'images/video/emulators.png', '', '', '', '814', '0', '0', '7', '23', 'yes', 'all', '0', '5', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '275', '17', '1354968600', '0', '0', 'emulators_005', 'wW-E9tGjlpg', '', '', 'Передача Эмуляторы про старые игрушки', '', 'Эмуляторы 005 - Вторжение ', '<p>Праздник дня всех святых не прошёл и мимо Эмуляторов. На повестке дня  жанр Сюрвайвал Хоррор (Survival horror) в составе игр &laquo;Незваные&raquo;  (Uninvited), &laquo;Кровавый выстрел&raquo; (Blood Shot) и &laquo;Часовая башня&raquo; (Clock  Tower) . Кто бы мог подумать, что этот день может обернуться такими  последствиями? Пугая и прикалываясь над Континуумом, Селект так увлёкся,  что случайно поспособствовал уничтожению главного игрового экрана, даже  не подозревая, кого он может этим самым выпустить на свободу.  (ПОСЛЕДНЯЯ СЕРИЯ ПЕРВОГО СЕЗОНА)</p>', '', '', '', '', '', 'NILinYT', 'http://www.youtube.com/user/NILinYT', '', 'images/video/emulators.png', '', '', '', '688', '0', '0', '8', '24', 'yes', 'all', '0', '1', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '276', '4', '1355573400', '0', '0', 'retro_tapki_1', 'TUAy0WKsqOs', '', '', 'Ретро тапки / эпизод 1 -Талисманы- ', '', 'Ретро тапки / эпизод 1 -Талисманы- ', '<p>Да, я это сделал! Это моя первая работа в данном стиле. Да и вообще  первая работа в плане видео. Периодически я пишу различные статьи на  тему игрового мира, но многие из моих друзей их не читают. Причина  проста, им просто лень. Поэтому я решил создать некую передачу, в  которой буду говорить впринципе всё то-же самое, что до этого писал, но  заодно делать различные интересные видео вставки. <br /><br />Выпуск  включает в себя весь текст из статьи, которую я написал для портала  tv-games.ru , несколько дополнительных вставок, и немного больше личного  мнения.</p>', '', '', '', '', '', '', '', '', 'images/video/retrotapki.png', '', '', '', '1155', '0', '0', '6', '18', 'yes', 'all', '0', '0', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '277', '17', '1356783000', '0', '0', 'emulators_006', 'tHd_2ayg3NU', '', '', 'Передача Эмуляторы про старые игрушки', '', 'Эмуляторы 006 - Дорогой Дедушка Мороз! ', '<p>Не смотря на небольшую катастрофу в студии и таинственное исчезновение Континуума, Селект во всеоружии выходит в эфир, чтобы поздравить зрителей с наступающими праздниками. На выручку ему приходит очаровательная Снегурочка, захватив с собой мешок с подарками и новогодними письмами зрителей. Составив праздничный дуэт они расскажут о целеустремлённом пингвинёнке Пэнде (Antarctic Adventure), отважных братьях снеговиках (Snow Bros. - Nick & Tom) и невероятных приключениях Санты Клауса (The Daze Before Christmas).</p>', '', '', '', '', '', 'NILinYT', 'http://www.youtube.com/user/NILinYT', '', 'images/video/emulators.png', '', '', '', '1177', '0', '0', '6', '18', 'yes', 'all', '0', '1', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '278', '4', '1357560600', '0', '0', 'retro_tapki_2', 'r5fUsmSO6_E', '', '', 'Ретро тапки / эпизод 2 -Эволюция звука в видеоиграх- ', '', 'Ретро тапки / эпизод 2 -Эволюция звука в видеоиграх- ', '<p>Музыка в видеоиграх прошла долгий и интересный путь, всё начиналось с простейших монотонных звуков, потом перешло в отдельный, иной от всех стандартов стиль музыки. И теперь музыка из видеоигр является частью мировог наследия. К шестому поколению домашних консолей границы игровой музыки окончательно стёрлись. Теперь любой композитор может создавать музыкальные произведения не оглядываясь на какие либо лимиты игровой системы. Со времён атари 2600 мы прошли долгий путь от 2х канального звука до 256 канального в современных системах. И хотя больше нету лимитов, эта техническая, синтезаторная музыка всё ещё живёт в популярном музыкальном стиле чиптюн.</p>', '', '', '', '', '', '', '', '', 'images/video/retrotapki.png', '', '', '', '1144', '0', '0', '8', '25', 'yes', 'all', '0', '1', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '279', '17', '1361710973', '0', '0', 'emulators_2x001', '-el0DoXh2wg', '', '', 'Передача Эмуляторы про старые игрушки', '', 'Эмуляторы 2х001 - Старый добрый боевик ', '<p>На плечи Селекта сваливается груз ответственности за будущее программы. Континуум оставил после себя кучу материала для будущих выпусков, но в одиночку со всем этим справиться нелегко. Селект даёт объявление об открытой вакансии на место второго ведущего и с нетерпением ожидает откликов. Но кто бы мог подумать, что первый же кандидат на столь ответственный пост, так озадачит бывалого геймера.</p>', '', '', '', '', '', 'NILinYT', 'http://www.youtube.com/user/NILinYT', '', 'images/video/emulators.png', '', '', '', '3148', '0', '0', '17', '57', 'yes', 'all', '0', '6', '');
