INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '281', '17', '1365063780', '0', '0', 'emulators_2x004x', 'http://vk.com/video_ext.php?oid=30190780&id=167332038&hash=464c000dd807092d&hd=1', '', '', 'Передача Эмуляторы про старые игрушки', '', 'Эмуляторы 2х004 - Закрытие (запрещён к показу)', '<p>С е р и я № 9:<br />Селект твёрдо намерен закрыть проект Эмуляторы,  когда-то основанный его напарником и другом Континуумом. Череда  внезапных и трагичных событий, подрывает в нём уверенность в собственных  силах. А главный виновник последнего происшествия - Старт, не оставляет  попыток исправить положение.</p>', '<p>Так как сюжетная линия сериала изменилась, эту серию убрали из вещания. Но для жителей нашего сайта ролик сбережён :)</p>', '', '', '', '', 'NILinYT', 'http://www.youtube.com/user/NILinYT', '', 'images/video/emulators.png', '', '', '', '1039', '0', '0', '6', '22', 'yes', 'all', '0', '3', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '282', '17', '1374593880', '0', '0', 'emulators_2x003', 'KrDBFnnKD4E', '', '', 'Передача Эмуляторы про старые игрушки', '', 'Эмуляторы 2х003 - Шпионские штучки ', '<p>С е р и я 9:<br />В первый же свой рабочий день Файнал решает навести  порядок. Пока мастер занимается починкой игрового экрана, новоиспечённый  дуэт заглянет за завесу тайн, скрываемых секретными службами и  организациями, которым было посвящено множество направлений  развлекательной индустрии: легендарный Джеймс Бонд (James Bond - The  Duel), спецотдел Миссия Невыполнима (Mission Impossible), и запутанная  история будущего (Flashback). Да ещё и за малышом нужно присмотреть  (Baby Boomer).  Увлёкшись этой темой, они не замечают, как сами  становятся ключевыми фигурами в чьей-то игре.</p>', '', '', '', '', '', 'NILinYT', 'http://www.youtube.com/user/NILinYT', '', 'images/video/emulators.png', '', '', '', '449', '0', '0', '4', '20', 'yes', 'all', '0', '4', '');
INSERT INTO danny_video ( downid, catid, public, stpublic, unpublic, cpu, locurl, exturl, extsize, descript, keywords, title, textshort, textmore, textnotice, mirrorsname, mirrorsurl, relisdown, authdown, sitedown, maildown, image, image_thumb, image_align, image_alt, hits, trans, lastdown, rating, totalrating, act, acc, listid, comments, tags) VALUES ( '283', '17', '1385172000', '0', '0', 'emulators_2x004', 'KZNnmiaRMqY', '', '', 'Передача Эмуляторы про старые игрушки', '', 'Эмуляторы 2х004 - Супергеройская история II - Противостояние', '<p>С е р и я 10:<br />Сценария больше нет. Но наши герои не собираются  опускать руки. Как выяснилось, Файнал разделяет любовь Селекта к  комиксам. А что может быть лучше, чем собраться за любимой приставкой и  обратиться к воспоминаниям о любимых супергероях? На ринге крутости  сойтутся главные конкуренты за приз зрительских симпатий - Бэтмен  (Batman), (Batman Return), (The Adventures of Batman and Robin) и  Супермен (Superman), (Death and Return of Superman), (Justice League  Task Force). Так что история, начало которой было положено Селектом и  Континуумом, продолжается.</p>', '', '', '', '', '', 'NILinYT', 'http://www.youtube.com/user/NILinYT', '', 'images/video/emulators.png', '', '', '', '469', '0', '0', '3', '11', 'yes', 'all', '0', '9', '');
